#!/bin/ksh
#-------------------------------------------
function GetDependancies {
	_NameObject=$(echo ${_Object}|awk -F "/" '{print $2}')
	case $_Type in
		tsk)
			_Session=${_NameObject}
			ListDepTask
			;;
		ses)
			_Session=${_NameObject}
			ListDepSession
			;;
		upr) 	
			_Uproc=${_NameObject}
			_TabUproc=${_Uproc}
			ListDepUproc
			;;
		class|res|rul) echo ${_Object} >> $_ExtractTmpDepFile ;;

		*) 	echo "${_Type}: Type inconnu!" ; exit 1 ;;
	esac
}
#-------------------------------------------
function GetTaskStatus {
_TaskStatus=$($UXEXE/uxshw tsk $_Space ses=$_Session node=$_Node upr=* mu=*|awk  '
								( $1 == "ITEMS" && $3 == "ses" ) {session=$5}
								( $2 == "hold" ){print "HOLD"}
								( $2 == "nohold" ){print "NOHOLD"}
								')
}
#-------------------------------------------
function GetTaskInfo {
$UXEXE/uxshw tsk $_Space ses=$_Session node=$_Node upr=* mu=*|awk  -v outputfile=$_ExtractTmpDepFile '
								( $1 == "ITEMS" && $3 == "ses" ) {session=$5}
								( $2 == "upr" ){uproc=$4}
								( $2 == "mu" ){print "tsk/"session"#"uproc"#"$4 >> outputfile}
								'
}
#-------------------------------------------
function ListDepTask {
_Session=$($UXEXE/uxshw tsk $_Space ses=$_Session node=$_Node upr=* mu=*|awk  -v outputfile=$_ExtractTmpDepFile '
								BEGIN {first="yes"}
								( $1 == "ITEMS" && $3 == "ses" && first == "yes") {first="no";print $5}
								( $1 == "ITEMS" && $3 == "ses" ) {session=$5}
								( $2 == "upr" ){uproc=$4}
								( $2 == "mu" ){print "tsk/"session"#"uproc"#"$4 >> outputfile}
								( $2 == "rule" ) {rule=substr($4,3,8)
										print "rul/"rule >> outputfile }
								')
ListDepSession
}
#-------------------------------------------
function ListDepSession {
_TabUproc=$($UXEXE/uxshw ses $_Space ses=$_Session node=$_Node|awk  -v outputfile=$_ExtractTmpDepFile '
								( $1 == "ITEMS" && $3 == "ses" ) {print "ses/"$5 >> outputfile} 
								( $2 == "upr" ){print $4;print "upr/"$4 >> outputfile}
								')
ListDepUproc
}
#-------------------------------------------
function ListDepUproc {
for _uproc in $_TabUproc
do
	$UXEXE/uxshw upr $_Space upr=$_uproc node=$_Node|awk  -v outputfile=$_ExtractTmpDepFile '
								( $1 == "ITEMS" && $3 == "upr" ) {print "upr/"$5 >> outputfile} 
								( $2 == "class" && $4 == " " ) {print "class/"$4 >> outputfile}
								( $1 == "dep" ) {print "upr/"$3 >> outputfile}
								( $1 == "res" ) {print "res/"$3 >> outputfile}
								( $2 == "dom" ){print "dom/"$4 >> outputfile}
								( $2 == "appl" ){print "appl/"$4 >> outputfile}
							'
done
}

def int import_obj(def String obj,def String importBaseDir,def String workBaseDir, def String envScript,def String espace, def String node, def String TskUser="", def String TskMu="", def String TskHold="",def String LogFile){
//	$DU_Script_on_remote${DU_env_s}UXEXE${DU_env_e}${DU_slash_env}uxext upr $DU_Espace NODE=$Noeud upr=$DU_Object output="$DU_Object" >> $Env_Path_Current/log/$Nom_Log
	def String objType=obj.replaceAll("/.*\$","").toLowerCase()
	def String objName=obj.replaceAll("^.*/","")
	logger("DEBUG","objType=$objType",LogFile)
	logger("DEBUG","objName=$objName",LogFile)

	def File importDir=new File(importBaseDir+File.separator+objType)
	logger("DEBUG","importDir:"+importDir,LogFile)

	def updateCommand=""
	def importCommand=""
	def UserTsk=""
	if(objType.equals("upr")){
		importCommand="uxins upr ${espace} node=${node} upr=\"$objName\" input=\""+objName+"\" repl"
	}
	else if(objType.equals("ses")){
		importCommand="uxins ses ${espace} node=${node} ses=\"$objName\" input=\""+objName+"\" repl"
	}
	else if(objType.equals("rul")){
		importCommand="uxins rul ${espace} node=${node} rul=\"$objName\" input=\""+objName+"\" repl"
	}
	else if(objType.equals("class")){
		importCommand="uxins adm  node=${node} classe=\"$objName\" input=\""+objName+"\" repl"
	}
	else if(objType.equals("res")){
		importCommand="uxins res ${espace} node=${node} res=\"$objName\" input=\""+objName+"\" repl"
	}
	else if(objType.equals("appl")){
		logger("WARNING","Import APPL demande mais non implemente !",LogFile)
		return 0

	}
	else if(objType.equals("dom")){
		logger("WARNING","Import DOM demande mais non implemente !",LogFile)
		return 0

	}
	else if(objType.equals("tsk")){
		def tabObjectName=objName.split("#")
		if(tabObjectName.size()!= 4){
			logger("ERROR","Le nom de l'objet ${obj} est invalide: une task doit etre de la forme tsk/uproc#ses#mu",LogFile)
			return 1
		}
		def String muName=""
		def uprocName=tabObjectName[1]
		def sessionName=tabObjectName[0]
		def holdmode=tabObjectName[3]
		if (TskMu=="") {
			muName=tabObjectName[2]
		} 
		else {
			muName=TskMu
		}

		importCommand="uxins tsk ${espace} node=${node} upr=\"$uprocName\" ses=\"$sessionName\" cible=\"$muName\"  HOLD input=\""+objName+"\" repl"
		// lancement de l'export Dollar U
		logger("DEBUG","importCommand:"+importCommand,LogFile)
		def Map result=wrapper_dollarU_cmdline(workBaseDir,envScript,importCommand,importDir.getCanonicalPath())

		if(result["exitcode"]!=0){
			logger("ERROR","import de l'objet ${obj} : KO \n"+result["stdout"]+"\n"+result["stderr"],LogFile)
			return 1
		}
		else{
			logger("INFO","import de la tache  ${obj} en mode HOLD : OK",LogFile)
			if ( TskHold != "" || TskUser != ""){
				if (TskHold != "") {
					holdmode=TskHold
					logger("DEBUG","update status task : "+TskHold,LogFile)
				}

				if (TskUser != "") {
					UserTsk="user="+TskUser
					logger("DEBUG","update user task : "+UserTsk,LogFile)
				}
				logger("DEBUG","lancement update",LogFile)
				logger("DEBUG","UserTsk:"+UserTsk,LogFile)
				updateCommand="uxupd tsk ${espace} node=${node} upr=\"$uprocName\" ses=\"$sessionName\" mu=\"$muName\"  vupr=* TECHINF ${UserTsk} ${holdmode}"

				logger("DEBUG","updateCommand:"+updateCommand,LogFile)
				// lancement de l'update Dollar U
				result=wrapper_dollarU_cmdline(workBaseDir,envScript,updateCommand,importDir.getCanonicalPath())

				if(result["exitcode"]!=0){
					logger("ERROR","update de l'objet ${obj} : KO \n"+result["stdout"]+"\n"+result["stderr"],LogFile)
					return 1
				}
				else{
					logger("INFO","update de la tache  ${obj} : OK",LogFile)

				}
			}
		}
	}
	else{
		logger("ERROR","Le type d'obj ${objType} n'est pas pris en charge",LogFile)
		return 1
	}

	// lancement de l'export Dollar U
	logger("DEBUG","importCommand:"+importCommand,LogFile)
	def Map result=wrapper_dollarU_cmdline(workBaseDir,envScript,importCommand,importDir.getCanonicalPath())

	if(result["exitcode"]!=0){
		logger("ERROR","import de l'objet ${obj} : KO \n"+result["stdout"]+"\n"+result["stderr"],LogFile)
		return 1
	}
	else{
		logger("INFO","import de l'objet ${obj} : OK",LogFile)
	}

	return 0
}

