def logger (def String level,def String message,def String outputFileStr=null) {
	def timpeStamp=new Date().format("yyyyMMdd-kkmmss")
	println "$timpeStamp:[$level] $message"
	if (outputFileStr!=null && outputFileStr !="" && new File("$outputFileStr").getParentFile().isDirectory()){
		def File outputFile = new File(outputFileStr)
		outputFile<< "$timpeStamp:[$level] $message\n"
	}
}

def wrapper_dollarU_cmdline(def String Tmpworkdir, def String envScript,def String cmdline, def String WorkDir){
	//def Map result=wrapper_dollarU_cmdline(workBaseDir,envScript,importCommand,importDir)
	def File tempFile
		println "[DEBUG] Tmpworkdir : $Tmpworkdir"
		println "[DEBUG] envScript : $envScript"
		println "[DEBUG] cmdline : $cmdline"
		println "[DEBUG] WorkDir : $WorkDir"		

	def Map result=[:]
	result["exitcode"]=1
	result["stderr"]=""
	result["stdout"]="Unknown"
	def command=""


	if (System.properties['os.name'].toLowerCase().contains('windows')) {
		tempFile=new File(Tmpworkdir+File.separator+"myshell.cmd")
	}
	else{

		tempFile=new File(Tmpworkdir+File.separator+"myshell.sh")
	}

	if(tempFile.exists()){
		if(! tempFile.delete()){
			result["exitcode"]=1
			result["stderr"]=""
			result["stdout"]="Impossible de supprimer "+tempFile.getAbsolutePath()
			return result
		}
	}

	if (System.properties['os.name'].toLowerCase().contains('windows')) {
		def output = tempFile.newDataOutputStream()
		output << "@echo off\r\n"
		output << "call ${envScript} > nul\r\n"
		output << "%UXEXE%\\${cmdline}\r\n"
		output << "exit /b %ERRORLEVEL%\r\n"
		output.close()

		command="cmd /c "+tempFile.getAbsolutePath()


	}
	else{
		def output = tempFile.newDataOutputStream()
		output << "${envScript} > /dev/null\n"
		output << "cd ${WorkDir}\n"
		output << "\${UXEXE}/${cmdline}\n"
		output << "exit \$?\n"
		output.close()

		command="sh "+tempFile.getAbsolutePath()

	}

	try{


		StringBuffer sbout = new StringBuffer()
		StringBuffer sberr = new StringBuffer()
		println "[DEBUG] command : $command"
		def process = command.execute() // Call *execute* on the string
		def tout = process.consumeProcessOutputStream(sbout)
		def terr = process.consumeProcessErrorStream(sberr)

		 process.waitFor()                               // Wait for the command to finish
	   // Obtain status and output
		 def value = process.exitValue()
		 result["exitcode"]=process.exitValue()
		 result["stderr"]=sberr.toString()
		 result["stdout"]=sbout.toString()


	}
	catch (Exception e){
		result["exitcode"]=1
		result["stderr"]=""
		result["stdout"]=e.getMessage()
	}
	finally{
		//tempFile.delete()
		return result
	}
	return result

}

