	if(!Env_Path_CurrentFile.deleteDir()){
		logger("ERROR","Impossible de supprimer le repertoire $Env_Path_Current",LogFile)
		System.exit(1)
	}
}

if(!Env_Path_CurrentFile.mkdirs()){
	logger("ERROR","Impossible de creer le repertoire $Env_Path_Current",LogFile)
	System.exit(1)
}

// Creation de repertoire temporaire
logger("INFO","Creation de repertoire temporaire :${Lst_Tmp_Directories}",LogFile)
Lst_Tmp_Directories.split(",").each {
	def String dir=it.trim()
	def File tmpDir=new File(Env_Path_CurrentFile,dir)
	if(!tmpDir.mkdirs()){
		logger("ERROR","Impossible de creer le repertoire "+Env_Path_CurrentFile+File.separator+dir,LogFile)
		System.exit(1)
	}
}
logger("INFO","Creation de repertoire temporaire :OK" 
System.exit(0)

// load function
${p:component/lib_logger}
${p:component/lib_wrapper_dollarU_cmdline}
${p:component/lib_export_obj}
${p:component/lib_import_obj}
${p:component/lib_delete_obj}
${p:component/lib_isExist_obj}




// Input SBM

def String Depot="${p:SBM_Depot}"
def String BaselineId="${p:SBM_BaselineId}"
def String Package="${p:SBM_Package}"
def String Folder="${p:SBM_Folder}"
def String Nom_Log="${p:SBM_Nom_Log}"
def String Dollar_U_Espace="${p:SBM_Dollar_U_Espace}"
def String Noeud="${p:SBM_Noeud}"
def String Script="${p:SBM_Script}"
def String BckPackage="${p:SBM_BckPackage}"

// Constante SRA
//Cst_DU_Lst_Tmp_Directories=tag,bck,log,tmp,pkg
//Cst_DU_Env_Path_Linux_Basedir="${Depot}/${BaselineId}"
//Cst_DU_Env_Path_Windows_Basedir="Z:\\SERENA\\${BaselineId}"
def String Env_Path_Linux="${p:environment/Cst_DU_Env_Path_Linux_Basedir}"+File.separator+"${BaselineId}"
def String Env_Path_Windows=="${p:environment/Cst_DU_Env_Path_Windows_Basedir}"+File.separator+"${BaselineId}"
def String Lst_Tmp_Directories="{p:component/Cst_DU_Lst_Tmp_Directories}" 
final def Boolean isWindows = (System.getProperty('os.name') =~ /(?i)windows/).find()

// Init Var Process
def String Env_Path_Current=""
def String LogFile="$Env_Path_Current"+File.separator+"log"+File.separator+Nom_Log

logger("INFO","DU_ENV_SET_CURRENT_ENV")
//****def String Env_Path="${Depot}/${BaselineId}"
if(isWindows){
	Env_Path_Current="${Env_Path_Windows}"
}
else{
	Env_Path_Current="${Env_Path_Linux}"
}

// decompression du package
 logger("INFO","Decompression du package "+"${Env_Path_Current}"+File.separator+"pkg"+File.separator+"${Package}.zip dans "+"${Env_Path_Current}"+File.separator+"pkg",LogFile)
 try{
	 def ant = new AntBuilder()// create an antbuilder

	 ant.unzip(  src:"${Env_Path_Current}"+File.separator+"pkg"+File.separator+"${Package}.zip",
			 dest:"${Env_Path_Current}"+File.separator+"pkg",
			 overwrite:"false"
	 )


 }
 catch (Exception e) {
	 e.printStackTrace()
	 logger("ERROR","Erreur durant la decompression du package "+"${Env_Path_Current}"+File.separator+"pkg"+File.separator+"${Package}.zip",LogFile)
	 logger("ERROR", e.getMessage(),LogFile)
	 System.exit(1)
 }

// Identification de la liste des objets a importer
 logger("INFO","Identification de la liste des objets devant etre importes ",LogFile)
 def ListofObjToImport=[]
 def File pkgBaseDir=new File("${Env_Path_Current}"+File.separator+"pkg")
 pkgBaseDir.eachFileRecurse(groovy.io.FileType.FILES){

	 def File objFile=it
	 def String objName=objFile.getName()
	 if(objName!= "${Package}.zip"){
		 def File parentFile=objFile.getParentFile()
		 def String typeName=parentFile.getName().toLowerCase()
		 if(typeName!= "tsk"){
			 ListofObjToImport.add(typeName+"/"+"$objName")
		 }
	 }

 }
 pkgBaseDir.eachFileRecurse(groovy.io.FileType.FILES){

	 def File objFile=it
	 def String objName=objFile.getName()
	 if(objName!= "${Package}.zip"){
		 def File parentFile=objFile.getParentFile()
		 def String typeName=parentFile.getName().toLowerCase()
		 if(typeName== "tsk"){
			 ListofObjToImport.add(typeName+"/"+"$objName")
		 }
	 }

 }
 logger("DEBUG","Liste des objets devant etre importes:"+ListofObjToImport.toString(),LogFile)


// Import de l'ensemble des objects
logger("INFO","Backup via un export de l'ensemble des objects deja existant devant etre reimportes",LogFile)
try{
	ListofObjToImport.unique().each{
		def String obj=it.trim()

		if(isExist_obj(obj,Env_Path_Current+File.separator+"tmp", Script,Dollar_U_Espace, Noeud,LogFile) ){
			logger("INFO","Backup de ${obj} ",LogFile)
			if(export_obj(obj,Env_Path_Current+File.separator+"bck",Env_Path_Current+File.separator+"tmp", Script,Dollar_U_Espace, Noeud,LogFile) != 0){
				throw new Exception("Arret du processus d'import")
			}
		}
		else{
			logger("INFO","L'objet ${obj} n'existe pas encore",LogFile)
		}
	}
}
catch(Exception e){
	logger("ERROR","Le processus de backup via export dollarU des objets a echouÃ©",LogFile)
	System.exit(1)
}

logger("INFO","Backup de l'ensemble des objets : OK",LogFile)

// creation du package : gzip $Env_Path_Current/pkg/$BckPackage.zip $Env_Path_Current/bck/*
 logger("INFO","Creation du package de backup:"+"${Env_Path_Current}"+File.separator+"pkg"+File.separator+"${BckPackage}.zip",LogFile)
 def String zipName="${Env_Path_Current}"+File.separator+"tmp"+File.separator+"${BckPackage}.zip"
 def String basedir="${Env_Path_Current}"+File.separator+"bck"
 try {
	 ant = new AntBuilder()
	 ant.zip(destfile: zipName, basedir:basedir)
 }
 catch (Exception e) {
	 logger("ERROR", "Error durant la creation du package ${zipName}:\n ${e.message}",LogFile)
	 System.exit(1)
 }

 logger("INFO","Creation du package de backup:OK"

System.exit(0)

