//SYB
// Common function

def logger (def String level,def String message,def String outputFileStr=null) {
	def timpeStamp=new Date().format("yyyyMMdd-kkmmss")
	println "$timpeStamp:[$level] $message"
	if (outputFileStr!=null && outputFileStr !="" && new File("$outputFileStr").getParentFile().isDirectory()){
		def File outputFile = new File(outputFileStr)
		outputFile<< "$timpeStamp:[$level] $message\n"
	}
}

def int execute_oracle_sql(def  envp=[],def String command,def File SqlWrkDir=null){

       def cr=0
       try{
             println "[I] Call isql ...."
			 def commandtab=[]
			 commandtab << "bash"
			 commandtab << "-c"
			 commandtab << command
//             println "[D] commandtab:"+commandtab

             //command="""cmd /c set"""

              StringBuffer sbout = new StringBuffer()
             StringBuffer sberr = new StringBuffer()
             def process = commandtab.execute(envp,SqlWrkDir)

             def tout = process.consumeProcessOutputStream(sbout)
             def terr = process.consumeProcessErrorStream(sberr)

               process.waitFor()                               // Wait for the command to finish

               def value = process.exitValue()

               if (value== 0){
                      cr=0
                      println sbout.toString()
                      println sberr.toString()
               }
               else{
                      cr=1
                      println sbout.toString()
                      println sberr.toString()
               }

       }
       catch(Exception e){
             cr=1
//           println "[E] Fail to execute_sql : cmd=$command\n Exception occured :\n"+e.getMessage()
             println "[E] Fail to execute_sql \n Exception occured :\n"+e.getMessage()

       }
       return cr
}

